INSERT INTO knowledge(question,answer,category) VALUES('चक्रवृद्धि ब्याज का सूत्र क्या है','चक्रवृद्धि ब्याज का सूत्र: A = P(1 + R/100)ⁿ। जहाँ n = वर्ष। CI = A - P।','तकनीक');
