INSERT INTO knowledge(question,answer,category) VALUES('वेग सूत्र का सूत्र क्या है','वेग सूत्र का सूत्र: v = d/t। जहाँ d = दूरी, t = समय। गति = दूरी/समय।','तकनीक');
